                        <textarea
                            required
                            value={recipe.ingredients}
                            onChange={(e) =>
                                setRecipe({
                                    ...recipe,
                                    ingredients: e.target.value,
                                })
                            }
                        ></textarea>
                        <h4>Directions:</h4>
                        <textarea
                            required
                            value={recipe.directions}
                            onChange={(e) =>
                                setRecipe({
                                    ...recipe,
                                    directions: e.target.value,
                                })
                            }
                        ></textarea>
                        <button>Submit</button>
                    </form>
                </div>
            </div>{" "}
            <div className="bg-container">
                <div className="bg">
                    <Home />
                </div>
            </div>
        </div>
    );
}

