import React, { useState } from "react";
import "../App.css";

function AddRecipe(props) {
    const [rName, setRName] = useState("");
    const [rIngredients, setRIngredients] = useState("");
    const [rDirections, setRDirections] = useState("");

    const [recipeList, setRecipeList] = useState([{}]);

    const handleSubmit = (e) => {
        e.preventDefault();
            const [info, setInfo] = useState({
        recipeName: "",
        ingredients: "",
        directions: "",
        id: 0,
    });
        setInfo(prevState => ({
            ...prevState,
            [recipeName]: rName,

        }))
    };

    return (
        <div>
            <form className="ingrForm" onSubmit={handleSubmit}>
                <h3>New Recipe</h3>
                <label>Recipe Name</label>
                <input
                    type="text"
                    name="recipe"
                    value={rName}
                    onChange={(e) => setRName(e.target.value)}
                    required
                ></input>
                <label>Ingredients</label>
                <textarea
                    required
                    name="ingredients"
                    value={rIngredients}
                    onChange={(e) => setRIngredients(e.target.value)}
                ></textarea>
                <label>Directions</label>
                <textarea
                    required
                    name="directions"
                    value={rDirections}
                    onChange={(e) => setRDirections(e.target.value)}
                ></textarea>
                <button>Submit</button>
            </form>
        </div>
    );
}
export default AddRecipe;
