import React, { useState, Component } from "react";
import RecipeList from "./RecipeList.js";
import "../App.css";

function AddRecipe() {
    const [info, setInfo] = useState({
        recipeName: "",
        ingredients: "",
        directions: "",
        id: 0,
    });
    const [recipeList, setRecipeList] = useState([]);
    const [isFormVisible, setIsFormVisible] = useState(true);

    const handleSubmit = (e) => {
        e.preventDefault();
        setInfo({ ...info, id: info.id + 1 });
        setRecipeList([...recipeList, info]);
        console.log(info);
        console.log(recipeList);
        setIs
    };

    return (
        <div>
            {isFormVisible ? (
                <form className="ingrForm" onSubmit={handleSubmit}>
                    <h3>New Recipe</h3>
                    <label>Recipe Name</label>
                    <input
                        type="text"
                        name="recipe"
                        value={info.recipeName}
                        onChange={(e) =>
                            setInfo({ ...info, recipeName: e.target.value })
                        }
                        required
                    ></input>
                    <label>Ingredients</label>
                    <textarea
                        required
                        name="ingredients"
                        value={info.ingredients}
                        onChange={(e) =>
                            setInfo({ ...info, ingredients: e.target.value })
                        }
                    ></textarea>
                    <label>Directions</label>
                    <textarea
                        required
                        name="directions"
                        value={info.directions}
                        onChange={(e) =>
                            setInfo({ ...info, directions: e.target.value })
                        }
                    ></textarea>
                    <button>Submit</button>
                </form>
            ) : (
                <RecipeList recipeList={recipeList} />
            )}
        </div>
    );
}
export default AddRecipe;
