import React, { useState } from "react";
import "../App.css";

function AddRecipe() {
    const [rName, setRName] = useState("");
    const [rIngredients, setRIngredients] = useState("");
    const [rDirections, setRDirections] = useState("");

    return (
        <div>
            <form className="ingrForm">
                <h3>New Recipe</h3>
                <label>Recipe Name</label>
                <input
                    type="text"
                    name="recipe"
                    value={rName}
                    onChange={setRName}
                    required
                ></input>
                <label>Ingredients</label>
                <textarea required name="ingredients" value={rIngredients}>
                    {" "}
                    onChange={setRIngredients}
                </textarea>
                <label>Directions</label>
                <textarea
                    required
                    name="directions"
                    value={rDirections}
                    onChange={setRDirections}
                ></textarea>
                <button>Submit</button>
            </form>
        </div>
    );
}
export default AddRecipe;
