import React, { useState, useEffect } from "react";

export default function useGetData(url) {
    const [error, setError] = useState("");
    const [data, setData] = useState(null);

    useEffect(() => {
        fetch(url)
            .then((res) => {
                if (!res.ok) {
                    throw Error("Error fetching");
                }
                return res.json();
            })
            .then((data) => {
                setData(data);
                setError(null);
            })
            .catch((err) => {
                setError(err);
            });
    }, [url]);

    return { data, error };
}
