import React, { useState } from "react";
import "../App.css";

function addRecipe() {
    const [rName, setRName] = useState("");
    const [rIngredients, setRIngredients] = useState("");
    const [rDirections, setRDirections] = useState("");

    return (
        <div>
            <form className="ingrForm">
                <h3>New Recipe</h3>
                <label>Recipe Name</label>
                <input type="text" name="recipe" value={rName} required></input>
                <label>Ingredients</label>
                <textarea
                    required
                    name="ingredients"
                    value={rIngredients}
                ></textarea>
                <label>Directions</label>
                <textarea required name="directions"></textarea>
                <button>Submit</button>
            </form>
        </div>
    );
}
export default addRecipe;
